var XWiki = (function(XWiki) {

var panels = XWiki.panels = XWiki.panels || {};

panels.MoreSAPOButtonListener = Class.create({
  initialize: function(button) {
    this.button = button;
    this.container = button.up('.sapoPanelMoreList').down('.sapoPanelMoreContainer');

    var moreAppsCount = this.container.select('.sapo-label').length;
    if (moreAppsCount > 0) {
      this.container.id = 'sapoPanelMoreContainer' + Math.floor(Math.random()*101);
      this.isToggling = false;
      this.button.observe('click', function(e) {
        e.stop();
        if (!this.isToggling) {
          this.toggle();
        }
      }.bindAsEventListener(this));
    } else {
      button.up('.sapoPanelMoreList').hide();
    }

    return this;
  },

  toggle: function() {
    this.isToggling = true;
    if (this.container.hasClassName('hidden')) {
      this.container.style.display = "none";
      this.container.removeClassName('hidden');
      Effect.BlindDown(this.container.id, {
        duration: 0.1,
        afterFinish: function() {
          this.isToggling = false;
        }.bind(this)
      });
    } else {
      Effect.BlindUp(this.container.id, {
        duration: 0.1,
        // We do this to be able to get our hidden element back, we don't rely on
        // display:none since we don't allow nested style in HTML elements.
        afterFinish: function() {
          this.container.addClassName('hidden');
          this.isToggling = false;
        }.bind(this)
      });
    }
  }
});

return XWiki;
}(XWiki || {}));

require(['scriptaculous/effects'], function() {
  // Activate all buttons already in the DOM.
  document.querySelectorAll('.sapoPanelMoreButton').forEach(function(button) {
    new XWiki.panels.MoreSAPOButtonListener(button);
  });
});

